CREATE TABLE `users` (  `UID` int(11) NOT NULL AUTO_INCREMENT,  `email` varchar(60) DEFAULT NULL,  `firstname` varchar(50) DEFAULT NULL,  `lastname` varchar(50) DEFAULT NULL,  `dob` date DEFAULT NULL,  `profile_image` longblob,  `bio` text,  `home` varchar(30) DEFAULT NULL,  `gender` varchar(10) DEFAULT NULL,  `password` varchar(30) DEFAULT NULL,  PRIMARY KEY (`UID`),  UNIQUE KEY `email` (`email`))CREATE TABLE `photo` (  `PID` int(11) NOT NULL AUTO_INCREMENT,  `UID` int(11) NOT NULL,  `AID` int(11) NOT NULL,  `data` longblob NOT NULL,  `caption` text,  `likes` int(11) DEFAULT '0',  PRIMARY KEY (`PID`),  KEY `UID` (`UID`),  KEY `AID` (`AID`),  CONSTRAINT `photo_ibfk_1` FOREIGN KEY (`UID`) REFERENCES `users` (`uid`) ON DELETE CASCADE,  CONSTRAINT `photo_ibfk_2` FOREIGN KEY (`AID`) REFERENCES `album` (`aid`) ON DELETE CASCADE,)CREATE TABLE `album` (  `AID` int(11) NOT NULL AUTO_INCREMENT,  `UID` int(11) NOT NULL,  `aname` varchar(50) DEFAULT NULL,  `adate` date DEFAULT NULL,  `cover` longblob,  PRIMARY KEY (`AID`),  KEY `UID` (`UID`),  CONSTRAINT `album_ibfk_1` FOREIGN KEY (`UID`) REFERENCES `users` (`uid`) ON DELETE CASCADE)CREATE TABLE `tag` (  `keyword` varchar(100) NOT NULL,  `PID` int(11) NOT NULL,  PRIMARY KEY (`keyword`,`PID`),  KEY `PID` (`PID`),  CONSTRAINT `tag_ibfk_1` FOREIGN KEY (`PID`) REFERENCES `photo` (`pid`) ON DELETE CASCADE)CREATE TABLE `like_photo` (  `UID` int(11) NOT NULL,  `PID` int(11) NOT NULL,  PRIMARY KEY (`UID`,`PID`),  KEY `PID` (`PID`),  CONSTRAINT `like_photo_ibfk_1` FOREIGN KEY (`UID`) REFERENCES `users` (`uid`) ON DELETE CASCADE,  CONSTRAINT `like_photo_ibfk_2` FOREIGN KEY (`PID`) REFERENCES `photo` (`pid`) ON DELETE CASCADE)CREATE TABLE `numTag` (  `keyword` varchar(100) NOT NULL,  `count` int(11) DEFAULT '0',  PRIMARY KEY (`keyword`),  CONSTRAINT `numtag_ibfk_2` FOREIGN KEY (`keyword`) REFERENCES `tag` (`keyword`) ON DELETE CASCADE)CREATE TABLE `friend` (  `UID` int(11) NOT NULL,  `FID` int(11) NOT NULL,  PRIMARY KEY (`UID`,`FID`),  KEY `FID` (`FID`),  CONSTRAINT `friend_ibfk_1` FOREIGN KEY (`UID`) REFERENCES `users` (`uid`) ON DELETE CASCADE,  CONSTRAINT `friend_ibfk_2` FOREIGN KEY (`FID`) REFERENCES `users` (`uid`) ON DELETE CASCADE)CREATE TABLE `comment` (  `CID` int(11) NOT NULL AUTO_INCREMENT,  `UID` int(11) NOT NULL,  `PID` int(11) NOT NULL,  `comment` text,  `cdate` date NOT NULL,  `commenter` varchar(40) DEFAULT NULL,  `commenter_image` longblob,  PRIMARY KEY (`CID`),  KEY `UID` (`UID`),  KEY `PID` (`PID`),  CONSTRAINT `comment_ibfk_1` FOREIGN KEY (`UID`) REFERENCES `users` (`uid`) ON DELETE CASCADE,  CONSTRAINT `comment_ibfk_2` FOREIGN KEY (`PID`) REFERENCES `photo` (`pid`) ON DELETE CASCADE)CREATE TABLE `scoreboard` (  `UID` int(11) NOT NULL,  `Score` int(11) DEFAULT '0',  PRIMARY KEY (`UID`),  CONSTRAINT `scoreboard_ibfk_1` FOREIGN KEY (`UID`) REFERENCES `users` (`uid`) ON DELETE CASCADE)